import React from 'react';

function ProductList() {
  // 배열 데이터
  const products = [
    { id: 1, info: { name: "사과", price: 1000 } },
    { id: 2, info: { name: "바나나", price: 1500 } },
    { id: 3, info: { name: "오렌지", price: 2000 } },
    { id: 4, info: { name: "포도", price: 2500 } },
  ];

  const elements = products.map((product)=>(
    <li 
    onClick={() => {
      내가만든함수(product.info.name);
    }}
    key={product.id}>{product.info.name} - {product.info.price}원</li>
  ));
  return (
    <div>
      <h1>상품 목록</h1>
      <ul>{elements}</ul>
    </div>
  );
}

const li = document.querySelector('li');
const key = document.querySelector('#key');

const alertName = () => {
  switch(key){
    case 1:
      alert("사과");
      break;
    case 2:
      alert("바나나");
      break;
    case 3:
      alert("오렌지");
      break;
    case 4:
      alert("포도");
      break;
  }
}

li.addEventListener('click',alertName);

export default ProductList;
